            if (invoiceInput.CurrencyOption.IsSet)
            {
                writer.WritePropertyName("currency");
                JsonSerializer.Serialize(writer, invoiceInput.Currency, jsonSerializerOptions);
            }
            if (invoiceInput.DueDateOption.IsSet)
                if (invoiceInput.DueDateOption.Value != null)
                    writer.WriteString("dueDate", invoiceInput.DueDateOption.Value!.Value.ToString(DueDateFormat));
                else
                    writer.WriteNull("dueDate");

            if (invoiceInput.SellerVatIdOption.IsSet)
                if (invoiceInput.SellerVatIdOption.Value != null)
                    writer.WriteString("sellerVatId", invoiceInput.SellerVatId);
                else
                    writer.WriteNull("sellerVatId");

            if (invoiceInput.SellerCountryOption.IsSet)
            {
                writer.WritePropertyName("sellerCountry");
                JsonSerializer.Serialize(writer, invoiceInput.SellerCountry, jsonSerializerOptions);
            }
            if (invoiceInput.BuyerIdOption.IsSet)
                if (invoiceInput.BuyerIdOption.Value != null)
                    writer.WriteString("buyerId", invoiceInput.BuyerId);
                else
                    writer.WriteNull("buyerId");

            if (invoiceInput.BuyerVatIdOption.IsSet)
                if (invoiceInput.BuyerVatIdOption.Value != null)
                    writer.WriteString("buyerVatId", invoiceInput.BuyerVatId);
                else
                    writer.WriteNull("buyerVatId");

            if (invoiceInput.ReferencedInvoiceIdOption.IsSet)
                if (invoiceInput.ReferencedInvoiceIdOption.Value != null)
                    writer.WriteString("referencedInvoiceId", invoiceInput.ReferencedInvoiceId);
                else
                    writer.WriteNull("referencedInvoiceId");

            if (invoiceInput.ReferencedInvoiceDateOption.IsSet)
                if (invoiceInput.ReferencedInvoiceDateOption.Value != null)
                    writer.WriteString("referencedInvoiceDate", invoiceInput.ReferencedInvoiceDateOption.Value!.Value.ToString(ReferencedInvoiceDateFormat));
                else
                    writer.WriteNull("referencedInvoiceDate");
        }
    }
